import React, { useState } from 'react';
import axios from 'axios';
import { PayPalButton } from 'react-paypal-button-v2';
import * as tf from '@tensorflow/tfjs';
import * as speechCommands from '@tensorflow-models/speech-commands';

const TranscriptForm = () => {
    const [url, setUrl] = useState('');
    const [message, setMessage] = useState('');

    const handleUrlChange = (e) => {
        setUrl(e.target.value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('Processing...');

        const videoId = extractVideoId(url);

        const audioContext = new (window.AudioContext || window.webkitAudioContext)();
        try {
            const audioStream = await fetchAudioStream(videoId, audioContext);
            const transcript = await processAudioStream(audioStream);
            setMessage('Transcript ready');
        } catch (error) {
            setMessage('Error processing audio');
        }
    };

    const extractVideoId = (url) => {
        const urlObj = new URL(url);
        return urlObj.searchParams.get('v');
    };

    const fetchAudioStream = async (videoId, audioContext) => {
        const response = await axios.get(`/api/get-audio-stream?videoId=${videoId}`, { responseType: 'arraybuffer' });
        const audioBuffer = await audioContext.decodeAudioData(response.data);
        return audioBuffer;
    };

    const processAudioStream = async (audioBuffer) => {
        const recognizer = speechCommands.create('BROWSER_FFT');
        await recognizer.ensureModelLoaded();

        const audioData = audioBuffer.getChannelData(0);
        const inputTensor = tf.tensor(audioData, [1, audioData.length]);

        const prediction = await recognizer.recognize(inputTensor);
        return prediction.transcript;
    };

    const handlePaymentSuccess = (details, data) => {
        setMessage('Payment successful. Download your transcript below.');
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label>
                    YouTube URL:
                    <input type="text" value={url} onChange={handleUrlChange} required />
                </label>
                <button type="submit">Get Transcript</button>
            </form>
            {message && <p>{message}</p>}
            {message === 'Transcript ready' && (
                <PayPalButton
                    amount="5.00"
                    onSuccess={(details, data) => handlePaymentSuccess(details, data)}
                />
            )}
        </div>
    );
};

export default TranscriptForm;
