import React, { useState } from 'react';
import axios from 'axios';
import { PayPalButtons, PayPalScriptProvider } from '@paypal/react-paypal-js';

const TranscriptForm = () => {
    const [url, setUrl] = useState('');
    const [message, setMessage] = useState('');
    const [transcript, setTranscript] = useState('');
    const [isPaymentReady, setIsPaymentReady] = useState(false);

    const handleUrlChange = (e) => {
        setUrl(e.target.value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('Processing...');

        const videoId = extractVideoId(url);
        console.log('Extracted videoId:', videoId);

        try {
            console.log('Fetching audio stream...');
            const audioBuffer = await fetchAudioStream(videoId);
            console.log('Audio stream fetched, processing audio...');
            const generatedTranscript = await processAudioStream(audioBuffer);
            console.log('Audio processed, setting transcript...');
            setTranscript(generatedTranscript);
            setMessage('Transcript ready. Please make the payment to download.');
            setIsPaymentReady(true);
        } catch (error) {
            console.error('Error during processing:', error.message);
            setMessage('Error processing audio');
        }
    };

    const extractVideoId = (url) => {
        const urlObj = new URL(url);
        const videoId = urlObj.searchParams.get('v') || urlObj.pathname.split('/').pop();
        if (!videoId) {
            throw new Error('Invalid YouTube URL');
        }
        return videoId;
    };

    const fetchAudioStream = async (videoId) => {
        try {
            const response = await axios.get(`http://localhost:5000/api/get-audio-stream?videoId=${videoId}`, { responseType: 'arraybuffer' });
            const audioContext = new (window.AudioContext || window.webkitAudioContext)();
            const audioBuffer = await audioContext.decodeAudioData(response.data);
            return audioBuffer;
        } catch (error) {
            console.error('Error fetching audio stream:', error.message);
            throw new Error('Error fetching audio stream');
        }
    };

    const processAudioStream = async (audioBuffer) => {
        return new Promise((resolve, reject) => {
            const offlineAudioContext = new OfflineAudioContext(1, audioBuffer.length, audioBuffer.sampleRate);
            const source = offlineAudioContext.createBufferSource();
            source.buffer = audioBuffer;
            source.connect(offlineAudioContext.destination);
            source.start(0);

            offlineAudioContext.startRendering().then((renderedBuffer) => {
                const audioData = renderedBuffer.getChannelData(0);
                const blob = new Blob([new DataView(audioData.buffer)], { type: 'audio/wav' });

                const reader = new FileReader();
                reader.onload = async () => {
                    const arrayBuffer = reader.result;
                    const speechRecognitionResult = await fetchTranscription(arrayBuffer);
                    resolve(speechRecognitionResult);
                };

                reader.onerror = (error) => {
                    console.error('Reader error:', error);
                    reject(new Error('Error reading audio data'));
                };

                reader.readAsArrayBuffer(blob);
            }).catch((error) => {
                console.error('Rendering error:', error);
                reject(new Error('Error rendering audio'));
            });
        });
    };

    const fetchTranscription = async (audioData) => {
        try {
            const response = await axios.post('http://localhost:5000/api/transcribe', audioData, {
                headers: {
                    'Content-Type': 'audio/wav'
                }
            });
            return response.data.transcript;
        } catch (error) {
            console.error('Error fetching transcription:', error.message);
            throw new Error('Error fetching transcription');
        }
    };

    const handlePaymentSuccess = () => {
        setMessage('Payment successful. Download your transcript below.');
        setIsPaymentReady(false);
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label>
                    YouTube URL:
                    <input type="text" value={url} onChange={handleUrlChange} required />
                </label>
                <button type="submit">Get Transcript</button>
            </form>
            {message && <p>{message}</p>}
            {isPaymentReady && (
                <PayPalScriptProvider options={{ "client-id": "YOUR_PAYPAL_CLIENT_ID" }}>
                    <PayPalButtons
                        style={{ layout: 'vertical' }}
                        createOrder={(data, actions) => {
                            return actions.order.create({
                                purchase_units: [{
                                    amount: {
                                        value: "5.00" // Set the correct amount
                                    }
                                }]
                            });
                        }}
                        onApprove={(data, actions) => {
                            return actions.order.capture().then(handlePaymentSuccess);
                        }}
                    />
                </PayPalScriptProvider>
            )}
            {message === 'Payment successful. Download your transcript below.' && (
                <div>
                    <p>{transcript}</p>
                    <a
                        href={`data:text/plain;charset=utf-8,${encodeURIComponent(transcript)}`}
                        download="transcript.txt"
                    >
                        Download Transcript
                    </a>
                </div>
            )}
        </div>
    );
};

export default TranscriptForm;
