import React, { useState } from 'react';
import axios from 'axios';
import { PayPalButtons, PayPalScriptProvider } from '@paypal/react-paypal-js';
import nlp from 'compromise';

const TranscriptForm = () => {
    const [url, setUrl] = useState('');
    const [message, setMessage] = useState('');
    const [transcript, setTranscript] = useState('');
    const [isPaymentReady, setIsPaymentReady] = useState(false);
    const [loading, setLoading] = useState(false);
    const [isNonEnglish, setIsNonEnglish] = useState(false);
    const [translate, setTranslate] = useState(false);

    const handleUrlChange = (e) => {
        setUrl(e.target.value);
    };

    const handleTranslateChange = (e) => {
        setTranslate(e.target.checked);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('Processing...');
        setLoading(true);
        setIsNonEnglish(false);

        try {
            const videoId = extractVideoId(url);
            console.log('Extracted video ID:', videoId);
            const audioBuffer = await fetchAudioStream(videoId);
            console.log('Fetched audio buffer');
            const generatedTranscript = await processAudioBuffer(audioBuffer);
            console.log('Generated transcript:', generatedTranscript);

            const language = detectLanguage(generatedTranscript);
            console.log('Detected language:', language);

            if (language !== 'en') {
                setIsNonEnglish(true);
                setMessage('Transcript available. This video is in a non-English language. You can get the transcript in the original language or translated to English for an extra fee.');
            } else {
                setMessage('Transcript available. Please make the payment to download.');
            }

            setTranscript(generatedTranscript);
            setIsPaymentReady(true);
        } catch (error) {
            setMessage(error.message);
            console.error('Error during processing:', error);
        } finally {
            setLoading(false);
        }
    };

    const extractVideoId = (url) => {
        try {
            const urlObj = new URL(url);
            const videoId = urlObj.searchParams.get('v') || urlObj.pathname.split('/').pop();
            if (!videoId) {
                throw new Error('Invalid YouTube URL');
            }
            return videoId;
        } catch (error) {
            throw new Error('Invalid URL format');
        }
    };

    const fetchAudioStream = async (videoId) => {
        try {
            const response = await axios.get(`http://localhost:5000/api/get-audio-stream?videoId=${videoId}`, { responseType: 'arraybuffer' });
            const audioContext = new (window.AudioContext || window.webkitAudioContext)();
            const audioBuffer = await audioContext.decodeAudioData(response.data);
            return audioBuffer;
        } catch (error) {
            throw new Error('Error fetching audio stream');
        }
    };

    const processAudioBuffer = async (audioBuffer) => {
        return new Promise((resolve, reject) => {
            const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
            recognition.lang = 'en-US';
            recognition.interimResults = false;
            recognition.maxAlternatives = 1;

            recognition.onresult = (event) => {
                const transcript = event.results[0][0].transcript;
                resolve(transcript);
            };

            recognition.onerror = (event) => {
                reject(new Error('Speech recognition error'));
            };

            recognition.onend = () => {
                reject(new Error('Speech recognition ended without results'));
            };

            // Create an offline audio context to process the audio buffer
            const offlineContext = new OfflineAudioContext(1, audioBuffer.length, audioBuffer.sampleRate);
            const source = offlineContext.createBufferSource();
            source.buffer = audioBuffer;
            source.connect(offlineContext.destination);
            source.start();

            offlineContext.startRendering().then((renderedBuffer) => {
                const audioData = renderedBuffer.getChannelData(0);
                const audioBlob = new Blob([audioData.buffer], { type: 'audio/wav' });
                const audioUrl = URL.createObjectURL(audioBlob);
                const audio = new Audio(audioUrl);
                audio.muted = true;

                audio.oncanplaythrough = () => {
                    recognition.start();
                    audio.play();
                };

                audio.onended = () => {
                    resolve('Transcript processing complete');
                };
            }).catch((error) => {
                reject(new Error('Offline rendering error'));
            });
        });
    };

    const detectLanguage = (text) => {
        const doc = nlp(text);
        const lang = doc.language();
        return lang.iso639_1 || 'en'; // Default to 'en' if language is not detected
    };

    const translateTranscript = async (text) => {
        try {
            const response = await axios.post(`http://localhost:5000/api/translate`, { text });
            return response.data.translatedText;
        } catch (error) {
            throw new Error('Error translating transcript');
        }
    };

    const handlePaymentSuccess = async () => {
        if (isNonEnglish && translate) {
            try {
                const translatedTranscript = await translateTranscript(transcript);
                setTranscript(translatedTranscript);
            } catch (error) {
                setMessage('Error translating transcript');
                return;
            }
        }
        setMessage('Payment successful. Download your transcript below.');
        setIsPaymentReady(false);
        // Save transcript to localStorage
        localStorage.setItem('transcript', transcript);
    };

    return (
        <div>
            <h1>YouTube Transcriber</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    YouTube URL:
                    <input type="text" value={url} onChange={handleUrlChange} required />
                </label>
                {isNonEnglish && (
                    <label>
                        Translate to English:
                        <input type="checkbox" checked={translate} onChange={handleTranslateChange} />
                    </label>
                )}
                <button type="submit" disabled={loading}>
                    {loading ? 'Processing...' : 'Get Transcript'}
                </button>
            </form>
            {message && <p>{message}</p>}
            {isPaymentReady && (
                <PayPalScriptProvider options={{ "client-id": process.env.REACT_APP_PAYPAL_CLIENT_ID }}>
                    <PayPalButtons
                        style={{ layout: 'vertical' }}
                        createOrder={(data, actions) => {
                            const amount = isNonEnglish ? "7.00" : "5.00";
                            return actions.order.create({
                                purchase_units: [{
                                    amount: {
                                        value: amount
                                    }
                                }]
                            });
                        }}
                        onApprove={(data, actions) => {
                            return actions.order.capture().then(handlePaymentSuccess);
                        }}
                    />
                </PayPalScriptProvider>
            )}
            {message === 'Payment successful. Download your transcript below.' && (
                <div>
                    <p>{transcript}</p>
                    <a
                        href={`data:text/plain;charset=utf-8,${encodeURIComponent(transcript)}`}
                        download="transcript.txt"
                    >
                        Download Transcript
                    </a>
                </div>
            )}
        </div>
    );
};

export default TranscriptForm;
